mapper.select:
SELECT t.id as a_id,t.title as a_title,t.content as a_content,t.user_id as a_user_id, t.release_time as a_release_time,t.see_num as a_see_num,t.column_id as a_column_id, t.enabled as a_enabled,t.create_data_time as a_create_data_time,t.create_data_username as a_create_data_username,t.update_data_time as a_update_data_time,t.update_data_username as a_update_data_username, u.nickname,(SELECT count(1) FROM reply_t rep WHERE rep.article_id = t.id) as replyNum, ta.id as t_id, ta.name as t_name, ta.code as t_code FROM article_t t LEFT JOIN user_t u ON u.id = t.user_id LEFT JOIN column_t c ON c.id = t.column_id LEFT JOIN article_tag_t att ON att.article_id = t.id LEFT JOIN tag_t ta ON ta.id = att.tag_id
AND t.title LIKE CONCAT('%',#{search},'%')
AND t.column_id = #{columnId}
AND c.code = #{column}
AND ta.code = #{tag}
AND t.enabled = #{enabled}
AND t.user_id = #{userId}
ORDER BY t.create_data_time DESC

mapper.insert:
INSERT INTO article_t  (
    id, title, content, user_id, release_time, see_num, column_id, enabled, create_data_time, create_data_username, update_data_time, update_data_username
)
VALUES(
    #{id}, #{title}, #{content}, #{userId}, #{releaseTime}, #{seeNum}, #{columnId}, #{enabled}, #{createDataTime}, #{createDataUsername}, #{updateDataTime}, #{updateDataUsername}
)
