mapper.insert:
INSERT INTO tag_t (
    id, name, enabled, create_data_time, create_data_username, update_data_time, update_data_username
)
VALUES(
    #{id}, #{name}, #{enabled}, #{createDataTime}, #{createDataUsername}, #{updateDataTime}, #{updateDataUsername}
)

mapper.update:
UPDATE tag_t SET
    name = #{name},
    enabled = #{enabled},
    update_data_time = #{updateDataTime},
    update_data_username = #{updateDataUsername}
WHERE id = #{id}
